                  </Card>
                </FuseAnimateGroup>
              </div>
            </div>
            <Button to={`/songs`} component={Link} className="justify-start px-32" color="secondary">
              Back
            </Button>
          </div>
        }
      />
    );
  }
}

export default withStyles(styles, { withTheme: true })(ActivityPage);
