import Formsy from 'formsy-react';
import React, { Component } from 'react';
import { TextFieldFormsy } from '@fuse';
import { bindActionCreators } from 'redux';
import { withRouter } from 'react-router-dom';
import connect from 'react-redux/es/connect/connect';
import * as authActions from 'app/auth/store/actions';
import { Button, InputAdornment, Icon } from '@material-ui/core';

class FirebaseLoginTab extends Component {
  state = {
    canSubmit: false
  };

  form = React.createRef();

  disableButton = () => {
    this.setState({ canSubmit: false });
  };

  enableButton = () => {
    this.setState({ canSubmit: true });
  };

  onSubmit = model => {
    window.location.replace('/activity');

    // this.props.submitLoginWithFireBase(model);
  };

  componentDidUpdate(prevProps, prevState) {
    if (this.props.login.error && (this.props.login.error.username || this.props.login.error.password)) {
      this.form.updateInputsWithError({
        ...this.props.login.error
      });

      this.props.login.error = null;
      this.disableButton();
    }

    return null;
  }

  render() {
    return (
      <div className="w-full">
        <Formsy
          onValidSubmit={this.onSubmit}
          onValid={this.enableButton}
          onInvalid={this.disableButton}
          ref={form => (this.form = form)}
          className="flex flex-col justify-center w-full"
        >
          <TextFieldFormsy
            className="mb-16"
            type="text"
            name="username"
            label="Email"
            validations={{
              minLength: 4
            }}
            validationErrors={{
              minLength: 'Min character length is 4'
            }}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <Icon className="text-20" color="action">
                    email
                  </Icon>
                </InputAdornment>
              )
            }}
            variant="outlined"
            required
          />

          <TextFieldFormsy
            className="mb-16"
            type="password"
            name="password"
            label="Password"
            validations={{
              minLength: 4
            }}
            validationErrors={{
              minLength: 'Min character length is 4'
            }}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <Icon className="text-20" color="action">
                    vpn_key
                  </Icon>
                </InputAdornment>
              )
            }}
            variant="outlined"
            required
          />

          <Button
            type="submit"
            variant="contained"
            color="primary"
            className="w-full mx-auto normal-case mt-16"
            aria-label="LOG IN"
            // disabled={!canSubmit}
            value="firebase"
          >
            Log in with Firebase
          </Button>
        </Formsy>
      </div>
    );
  }
}

function mapDispatchToProps(dispatch) {
  return bindActionCreators(
    {
      submitLoginWithFireBase: authActions.submitLoginWithFireBase
    },
    dispatch
  );
}

function mapStateToProps({ auth }) {
  return {
    login: auth.login,
    user: auth.user
  };
}

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(FirebaseLoginTab)
);
